let preferedColorMode=localStorage.getItem("colorMode");if(console.log(preferedColorMode),preferedColorMode)preferedColorMode==="dark"?(document.body.classList.remove("light"),document.body.classList.add("dark")):(document.body.classList.remove("dark"),document.body.classList.add("light"));else{const e=window.matchMedia("(prefers-color-scheme: light)");e.matches?(document.body.classList.remove("dark"),document.body.classList.add("light")):(document.body.classList.remove("light"),document.body.classList.add("dark"))}document.querySelector("#switcherLightMode").addEventListener("click",()=>{document.body.classList.remove("dark"),document.body.classList.add("light"),localStorage.setItem("colorMode","light"),console.log(localStorage.getItem("colorMode"))}),document.querySelector("#switcherDarkMode").addEventListener("click",()=>{document.body.classList.remove("light"),document.body.classList.add("dark"),localStorage.setItem("colorMode","dark"),console.log(localStorage.getItem("colorMode"))})